
public class ReverseInteger {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 1234;
		int b;
		
		while(a>0) {
			
			b=a%10;  //modulas o/p is remainder
			
			System.out.print(b);
			
			a=a/10;  //division o/p is quotient
		
		}
		
		
		
		
		
		
		
		
		
		
		
	}

}
